$('.btn-file input').fileupload({
	dataType: 'json',
	progressall: function(e, d) {
		var progress = parseInt(d.loaded / d.total * 100, 10);
		var $o = $(this).parent('span').next('.upload-progress');
		$o.css('width', progress + '%');
	},
	done: function(e, d) {
    var $o = $(this).parent('span'), upload = d.result;
    $o.data('upload_id', upload.id)
      .trigger('uploaded.ir.upload', upload);
	}
});
